SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_caja,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_caja_sp_eliminar_retencion (P_ID_RETENCION bigint) AS $body$
DECLARE


        L_ID_TIPOORIGEN bigint;
        L_ID_ORIGEN bigint;

        c_ret_compra CURSOR FOR	
        SELECT ID_TIPOORIGEN, ID_RETDETALLE
        FROM CAJA_RETENCION_COMPRA 
        WHERE ID_RETENCION=P_ID_RETENCION;

        
BEGIN
        
        OPEN c_ret_compra;
        FETCH c_ret_compra INTO L_ID_TIPOORIGEN, L_ID_ORIGEN;

        WHILE c_ret_compra%FOUND LOOP

            DELETE FROM CONTA_ASIENTO WHERE ID_TIPOORIGEN = L_ID_TIPOORIGEN AND ID_ORIGEN=P_ID_RETENCION;

            FETCH c_ret_compra INTO L_ID_TIPOORIGEN, L_ID_ORIGEN;
        END LOOP;
        CLOSE c_ret_compra;
        
        DELETE FROM CAJA_RETENCION_COMPRA where ID_RETENCION = P_ID_RETENCION;
        DELETE FROM CAJA_RETENCION where ID_RETENCION = P_ID_RETENCION;

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
