SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_finances_students,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_finances_students_sp_desc_vice_mat (P_ID_ALUMNO_CONTRATO bigint,P_ID_CRITERIO_SEMESTRE bigint,P_TIPO_ALUMNO text,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

    l_items bigint;
    l_contar bigint;
    l_error bigint:=0;
    l_msgerror varchar(200):='';

    l_id_semestre_programa bigint;
    l_id_persona bigint;

    l_id_criterio bigint;
    l_importe decimal(10,2);
    l_dc varchar(5);
    l_dias decimal(10,2);
    l_nombre varchar(150);
    l_tipo_valor varchar(2);

BEGIN
    SELECT ID_SEMESTRE_PROGRAMA,ID_PERSONA 
    INTO STRICT l_id_semestre_programa,l_id_persona
    FROM DAVID.ACAD_ALUMNO_CONTRATO
    WHERE ID_ALUMNO_CONTRATO = P_ID_ALUMNO_CONTRATO;

    SELECT COUNT(*) INTO STRICT l_contar FROM FIN_ALUMNO_DESCUENTO_VICE 
    WHERE ID_PERSONA=l_id_persona
    and tipo_dscto='V'
    AND ESTADO='1';

    IF l_contar>0 THEN
    
      SELECT COALESCE(MATRICULA,0),TIPO_MAT INTO STRICT l_importe,l_tipo_valor FROM FIN_ALUMNO_DESCUENTO_VICE 
      WHERE ID_PERSONA=l_id_persona
      and tipo_dscto='V'
      AND ESTADO='1';

      
 
      if l_importe>0 then
        SELECT 
            ID_CRITERIO,
            DC,
            NOMBRE
            INTO STRICT
            l_id_criterio,
            l_dc,
            l_nombre
        FROM VW_MAT_CRITERIO_SEMESTRE
        WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE;

        SELECT COALESCE(MAX(ITEM),0)+1  INTO STRICT l_items FROM MAT_CONCEPTO_ASIGN_ALUM
       WHERE ID_ALUMNO_CONTRATO=P_ID_ALUMNO_CONTRATO;

       insert into MAT_CONCEPTO_ASIGN_ALUM(
        ID_ALUMNO_CONTRATO,
        ID_CRITERIO_SEMESTRE,
        ITEM,
        ID_SEMESTRE_PROGRAMA,
        DESCRIPCION,
        DC,
        TOTAL,
        TIPO_VALOR
        )VALUES (
        P_ID_ALUMNO_CONTRATO,
        P_ID_CRITERIO_SEMESTRE,
        l_items,
        l_id_semestre_programa,
        l_nombre,
        l_dc,
        l_importe,
        l_tipo_valor
        );
      end if;

    END IF;

    P_ERROR:=l_error;
    P_MSGERROR:= l_msgerror;

  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
