SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_finances_students,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_finances_students_sp_mat_beca18 (P_ID_ALUMNO_CONTRATO bigint,P_ID_CRITERIO_SEMESTRE bigint,P_TIPO_ALUMNO text,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

    l_items bigint;
    l_contar bigint;
    l_error bigint:=0;
    l_msgerror varchar(200):='';
    l_id_semestre_programa bigint;
    l_id_persona bigint;

    l_id_criterio	bigint;
    l_importe	decimal(10,2);
    l_tipo_proceso	varchar(2);
    l_tipo_valor	varchar(2);
    l_id_semestre bigint;
    l_nombre varchar(150);
    l_id_plan_programa bigint;
    l_id_plan bigint;
    l_dc varchar(1);

BEGIN
  
    SELECT ID_SEMESTRE_PROGRAMA,ID_PERSONA,ID_PLAN_PROGRAMA
    INTO STRICT l_id_semestre_programa,l_id_persona,l_id_plan_programa
    FROM DAVID.ACAD_ALUMNO_CONTRATO
    WHERE ID_ALUMNO_CONTRATO = P_ID_ALUMNO_CONTRATO;

    
    SELECT 
      ID_CRITERIO,
      IMPORTE,
      TIPO_PROCESO,
      TIPO_VALOR,
      DC,
      nombre
      INTO STRICT
      l_id_criterio,
      l_importe,
      l_tipo_proceso,
      l_tipo_valor,
      l_dc,
      l_nombre
    FROM VW_MAT_CRITERIO_SEMESTRE
    WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE;

    if l_tipo_proceso = 'P' then
      SELECT ID_PLAN INTO STRICT l_id_plan from DAVID.ACAD_PLAN_PROGRAMA WHERE ID_PLAN_PROGRAMA=l_id_plan_programa;

      SELECT COUNT(*) INTO STRICT l_contar  FROM MAT_PLAN_COSTO a, DAVID.ACAD_PLAN b
      WHERE a.id_plan=b.id_plan
      and a.ID_SEMESTRE_PROGRAMA =l_id_semestre_programa
      AND a.ID_CRITERIO=l_id_criterio
      AND a.ID_PLAN= l_id_plan;

      IF l_contar>0 THEN
        if l_contar=1 then
          SELECT a.IMPORTE,b.NOMBRE INTO STRICT l_importe,l_nombre 
          FROM MAT_PLAN_COSTO a, DAVID.ACAD_PLAN b
          WHERE a.id_plan=b.id_plan
          and a.ID_SEMESTRE_PROGRAMA =l_id_semestre_programa
          AND a.ID_CRITERIO=l_id_criterio
          AND a.ID_PLAN= l_id_plan;

          if coalesce(l_importe,0)=0 then
            l_error:=1;
            l_msgerror:='Costo de matrícula igual a cero';
          else
           SELECT COALESCE(MAX(ITEM),0)+1  INTO STRICT l_items FROM MAT_CONCEPTO_ASIGN_ALUM
           WHERE ID_ALUMNO_CONTRATO=P_ID_ALUMNO_CONTRATO;

           insert into MAT_CONCEPTO_ASIGN_ALUM(
            ID_ALUMNO_CONTRATO,
            ID_CRITERIO_SEMESTRE,
            ITEM,
            ID_SEMESTRE_PROGRAMA,
            DESCRIPCION,
            DC,
            TOTAL,
            TIPO_VALOR
            )VALUES (
            P_ID_ALUMNO_CONTRATO,
            P_ID_CRITERIO_SEMESTRE,
            l_items,
            l_id_semestre_programa,
            'Matrícula plan '||l_nombre,
            l_dc,
            l_importe,
            l_tipo_valor
            );
          end if;
        ELSE
          l_error:=1;
          l_msgerror:='Esta asignado más de un costo de matrícula';
        END IF;
      ELSE
          l_error:=1;
          l_msgerror:='No esta asignado costo de matrículasss: '||P_ID_CRITERIO_SEMESTRE;
      END IF;
    else
      if l_importe>0 then
       SELECT COALESCE(MAX(ITEM),0)+1  INTO STRICT l_items FROM MAT_CONCEPTO_ASIGN_ALUM
           WHERE ID_ALUMNO_CONTRATO=P_ID_ALUMNO_CONTRATO;

       insert into MAT_CONCEPTO_ASIGN_ALUM(
        ID_ALUMNO_CONTRATO,
        ID_CRITERIO_SEMESTRE,
        ITEM,
        ID_SEMESTRE_PROGRAMA,
        DESCRIPCION,
        DC,
        TOTAL,
        TIPO_VALOR
        )VALUES (
        P_ID_ALUMNO_CONTRATO,
        P_ID_CRITERIO_SEMESTRE,
        l_items,
        l_id_semestre_programa,
        l_nombre,
        l_dc,
        l_importe,
        l_tipo_valor
        );
      else
        l_error:=1;
        l_msgerror:='No esta asignado costo de matrícula';
      end if;
    end if;
    P_ERROR:=l_error;
    P_MSGERROR:= l_msgerror;

  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eliseo.pkg_finances_students_sp_mat_beca18 (P_ID_ALUMNO_CONTRATO bigint,P_ID_CRITERIO_SEMESTRE bigint,P_TIPO_ALUMNO text,P_ERROR OUT bigint,P_MSGERROR out text) FROM PUBLIC;
