CREATE OR REPLACE FUNCTION eliseo.pkg_inventories_fc_articulo_servicio (P_ID_ALMACEN bigint,P_ID_ARTICULO bigint,P_ID_ANHO bigint) RETURNS varchar AS $body$
DECLARE

    P_NOMBRE varchar(255);
    L_CANT bigint;

BEGIN
    
        SELECT count(1) INTO STRICT L_CANT 
        FROM INVENTARIO_ALMACEN_ARTICULO
        WHERE ID_ALMACEN = P_ID_ALMACEN
        AND ID_ARTICULO = P_ID_ARTICULO
        AND ID_ANHO = P_ID_ANHO;

        IF L_CANT > 0 THEN 
            SELECT coalesce(ES_SERVICIO,'N') INTO STRICT P_NOMBRE 
            FROM INVENTARIO_ALMACEN_ARTICULO
            WHERE ID_ALMACEN = P_ID_ALMACEN
            AND ID_ARTICULO = P_ID_ARTICULO
            AND ID_ANHO = P_ID_ANHO;
        ELSE
            P_NOMBRE := 'S';
        END IF;
        RETURN(P_NOMBRE);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
