CREATE OR REPLACE FUNCTION eliseo.pkg_orders_fc_depto (P_ID_SEDEAREA bigint) RETURNS varchar AS $body$
DECLARE

    L_NOMBRE varchar(255);

BEGIN
        SELECT 
                coalesce(B.ID_DEPTO,'') AS ID_DEPTO INTO STRICT L_NOMBRE
        FROM ORG_AREA A, ORG_SEDE_AREA B
        WHERE A.ID_AREA = B.ID_AREA 
        AND B.ID_SEDEAREA = P_ID_SEDEAREA;

        RETURN(L_NOMBRE);
    END;
$body$
LANGUAGE PLPGSQL
