CREATE OR REPLACE PROCEDURE eliseo.pkg_presupuesto_sp_valida_presupuesto (P_ID_PRESUPUESTO bigint,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

      l_error bigint:=0;
      l_msgerror varchar(200):='';
      l_contar bigint;
      l_estado varchar(2);
      l_estado_asi varchar(2);
      l_msg varchar(200):='';

BEGIN
       SELECT ESTADO,case when ESTADO='0' then 'Anulado' when ESTADO='2' then 'Aprobado'  end 
       into STRICT l_estado,l_msg FROM PSTO_PRESUPUESTO WHERE ID_PRESUPUESTO=P_ID_PRESUPUESTO;

       if l_estado<>'1' then
          l_error:=1;
          l_msgerror:='Presupuesto '||l_msg;
       end if;

       if l_error=0 then
       
         SELECT count(*) into STRICT l_contar FROM PSTO_ASIENTO WHERE ID_PRESUPUESTO=P_ID_PRESUPUESTO and ESTADO='2';

         if l_contar>0 then
            l_error:=1;
            l_msgerror:='Asiento Aprobado';
         end if;
       end if;

       
       P_ERROR:=l_error;
       P_MSGERROR:=l_msgerror;
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
