SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_prof,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_prof_sp_desc_promotora (P_ID_PROFORMA bigint,P_ID_CRITERIO_SEMESTRE bigint,P_ERROR OUT bigint,P_MSGERROR out text) AS $body$
DECLARE

    l_items bigint;
    l_contar bigint;
    l_error bigint:=0;
    l_msgerror varchar(200):='';
    l_id_semestre_programa bigint;
    l_id_persona bigint;

    l_id_criterio	bigint;
    l_importe	decimal(10,2);
    l_tipo_proceso	varchar(2);
    l_tipo_valor	varchar(2);
    l_nombre varchar(150);
    l_dc varchar(1);
    l_creditos decimal(10,2);
    l_id_postulante bigint;
    l_so varchar(10);

BEGIN
  
    
    SELECT B.ID_PERSONA, A.ID_SEMESTRE_PROGRAMA,B.ID_POSTULANTE,coalesce(A.SOSTENEDOR_OBRA,'N')
    INTO STRICT l_id_persona,l_id_semestre_programa,l_id_postulante,l_so
    FROM ELISEO.PRO_PROFORMA A, PRO_POSTULANTE B
    WHERE A.ID_POSTULANTE=B.ID_POSTULANTE
    AND A.ID_PROFORMA = P_ID_PROFORMA;

    if l_so='S' then
      SELECT 
        ID_CRITERIO,
        IMPORTE,
        TIPO_PROCESO,
        TIPO_VALOR,
        DC,
        NOMBRE
        INTO STRICT
        l_id_criterio,
        l_importe,
        l_tipo_proceso,
        l_tipo_valor,
        l_dc,
        l_nombre
      FROM VW_MAT_CRITERIO_SEMESTRE
      WHERE ID_CRITERIO_SEMESTRE=P_ID_CRITERIO_SEMESTRE;

       
      SELECT COALESCE(MAX(ITEM),0)+1  INTO STRICT l_items FROM PRO_CONCEPTO_ASIGN_ALUM
      WHERE ID_PROFORMA=P_ID_PROFORMA;

      
      insert into PRO_CONCEPTO_ASIGN_ALUM(
          ID_PROFORMA,
          ID_CRITERIO_SEMESTRE,
          ITEM,
          ID_SEMESTRE_PROGRAMA,
          DESCRIPCION,
          DC,
          TOTAL,
          TIPO_VALOR
          )VALUES (
          P_ID_PROFORMA,
          P_ID_CRITERIO_SEMESTRE,
          l_items,
          l_id_semestre_programa,
          l_nombre,
          l_dc,
          l_importe,
          l_tipo_valor
          );
    end if;
--     <<salida>>
    P_ERROR:=l_error;
    P_MSGERROR:= l_msgerror;
  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
