SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_purchases,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_purchases_fc_fecha_autorizado (P_ID_REGISTRO bigint) RETURNS varchar AS $body$
DECLARE

        FECHA varchar(100);

BEGIN
        SELECT FECHA INTO STRICT FECHA
        FROM (
            SELECT TO_CHAR(A.FECHA,'DD/MM/YYYY HH:MI:SS') AS FECHA FROM PROCESS_PASO_RUN A JOIN PROCESS_PASO B ON A.ID_PASO = B.ID_PASO 
            JOIN USERS X ON X.ID = A.ID_PERSONA
            WHERE ID_REGISTRO = P_ID_REGISTRO 
            AND A.ESTADO = 1 ORDER BY ID_DETALLE DESC
        ) alias1 LIMIT 1;

        RETURN(FECHA);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
