CREATE OR REPLACE FUNCTION eliseo.pkg_purchases_orders_fc_estado_voucher_compra (P_ID_COMPRA bigint) RETURNS varchar AS $body$
DECLARE

        L_ESTADO_VOUCHER varchar(4);

BEGIN
        SELECT coalesce(B.ACTIVO,'N') INTO STRICT L_ESTADO_VOUCHER
        FROM COMPRA A JOIN CONTA_VOUCHER B ON A.ID_VOUCHER = B.ID_VOUCHER
