SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_purchases_orders,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_purchases_orders_fc_porcentaje_proceso (P_ID_PEDIDO bigint) RETURNS varchar AS $body$
DECLARE

        PORCENTAJE varchar(100) := '0';
        L_PASOS_EJECUTADOS bigint;
        L_ID_DEXEC bigint;
        L_ID_EXECUTE bigint;
        L_CANT bigint;

BEGIN
    
        SELECT ID_DEXEC INTO STRICT l_ID_DEXEC FROM PEDIDO_REGISTRO A WHERE A.ID_PEDIDO = P_ID_PEDIDO;

        SELECT COUNT(1) INTO STRICT L_CANT FROM LAMB_PROCESS_EXEC_PASO WHERE ID_DEXEC = l_ID_DEXEC;

        IF L_CANT > 0 THEN 

            SELECT ID_EXECUTE INTO STRICT L_ID_EXECUTE FROM LAMB_PROCESS_EXEC_PASO WHERE ID_DEXEC = l_ID_DEXEC;

            SELECT COUNT(1) INTO STRICT L_PASOS_EJECUTADOS FROM LAMB_PROCESS_EXEC_PASO WHERE ID_EXECUTE = L_ID_EXECUTE;

            SELECT ROUND((L_PASOS_EJECUTADOS*100)/(COUNT(B.ID_FLUJO)-1),0) INTO STRICT PORCENTAJE 
            FROM PEDIDO_REGISTRO A JOIN LAMB_PROCESS_FLUJO B ON A.ID_TASK = B.ID_TASK 
            LEFT JOIN LAMB_PROCESS_EXEC_PASO C ON A.ID_DEXEC = C.ID_DEXEC 
            AND B.ID_PASO = C.ID_PASO_ACTUAL
            WHERE A.ID_PEDIDO = P_ID_PEDIDO;
        ELSE
            PORCENTAJE := '0';
        END IF;

        RETURN(PORCENTAJE);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
