SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_purchases_orders,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_purchases_orders_fc_user_paso (P_ID_DEXEC bigint,P_CODIGO text) RETURNS varchar AS $body$
DECLARE

        L_ID_EXECUTE bigint;
        NOM_PERSONA varchar(100);

BEGIN
        SELECT DISTINCT ID_EXECUTE INTO STRICT L_ID_EXECUTE FROM LAMB_PROCESS_EXEC_PASO WHERE ID_DEXEC = P_ID_DEXEC;
        SELECT EMAIL INTO STRICT NOM_PERSONA FROM (
	        SELECT
	                C.EMAIL
	        FROM LAMB_PROCESS_EXEC_PASO A JOIN LAMB_PROCESS_PASO B ON A.ID_PASO_ACTUAL = B.ID_PASO AND A.ESTADO <> 3
	        JOIN USERS C ON A.ID_PERSONA = C.ID
	        WHERE A.ID_EXECUTE = L_ID_EXECUTE
	        AND B.CODIGO = P_CODIGO
	        ORDER BY A.ID_DEXEC DESC
	    ) alias0 LIMIT 1;

        RETURN(NOM_PERSONA);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
