CREATE OR REPLACE FUNCTION eliseo.pkg_sales_fc_cliente_direccion (P_ID_PERSONA bigint) RETURNS varchar AS $body$
DECLARE

    L_DIRECCION varchar(255);

BEGIN
        SELECT Min(coalesce(DIRECCION,'')) INTO STRICT L_DIRECCION
        FROM MOISES.PERSONA_DIRECCION
        WHERE ID_PERSONA = P_ID_PERSONA
        AND ID_TIPODIRECCION = 5;

        RETURN(L_DIRECCION);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
