SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_sales_sp_eliminar_venta_asiento (P_ID_VDETALLE bigint,P_ERROR OUT bigint, P_MSN OUT text) AS $body$
DECLARE

        L_ID_VENTA bigint;
        L_ESTADO bigint;

BEGIN
            SELECT DISTINCT ID_VENTA INTO STRICT L_ID_VENTA FROM VENTA_DETALLE
            WHERE ID_VDETALLE = P_ID_VDETALLE;
            SELECT ESTADO INTO STRICT L_ESTADO FROM VENTA WHERE ID_VENTA = L_ID_VENTA;
            IF L_ESTADO = 0 THEN
                DELETE FROM VENTA_ASIENTO WHERE ID_VDETALLE = P_ID_VDETALLE;
                DELETE FROM VENTA_DETALLE WHERE ID_VDETALLE = P_ID_VDETALLE;
                P_ERROR := 0;
                P_MSN := 'OK, Eliminada';
            ELSE
                P_ERROR := 1;
                P_MSN := 'ERROR: No se puede Eliminar, la venta ya esta Procesada';
            END IF;

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
