SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_sales_sp_venta_electronica_aces (P_ID_VENTA bigint,P_ID_COMPROBANTE text) AS $body$
DECLARE

        L_ID_ENTIDAD bigint;
        L_RUC varchar(11);
        L_ID_COMPROBANTE varchar(2);
        L_SERIE varchar(4);
        L_NUMERO varchar(8);
       	L_ES_EXPORTACION varchar(1);
        --L_DET varchar(4000);
        L_DET text;
        L_EMISOR_ID bigint:=101; --acesssss EMPRESA CON RUC UPeU
        L_HASH varchar(50);
        	

BEGIN  
        SELECT 
                ID_ENTIDAD,ID_COMPROBANTE,SERIE,NUMERO, ES_EXPORTACION
                INTO STRICT L_ID_ENTIDAD,L_ID_COMPROBANTE,L_SERIE,L_NUMERO,L_ES_EXPORTACION
        FROM VENTA
        WHERE ID_VENTA = P_ID_VENTA;

        SELECT B.ID_RUC INTO STRICT L_RUC
        FROM CONTA_ENTIDAD A, CONTA_EMPRESA B
        WHERE A.ID_EMPRESA = B.ID_EMPRESA
        AND A.ID_ENTIDAD = L_ID_ENTIDAD;

        IF (P_ID_COMPROBANTE = '03' OR P_ID_COMPROBANTE = '01' )THEN
            IF L_ES_EXPORTACION='1' THEN 
            	SELECT (pkg_sales_industry_fc_venta_exportacion(P_ID_VENTA)) INTO STRICT L_DET;
            ELSE
            	SELECT (pkg_sales_industry_fc_venta(P_ID_VENTA)) INTO STRICT L_DET;
            END IF;
        END IF;
        IF (P_ID_COMPROBANTE = '07' OR P_ID_COMPROBANTE = '08' )THEN
            SELECT (pkg_sales_industry_fc_nota(P_ID_VENTA)) INTO STRICT L_DET;
        END IF;

        --FACTURACION ELECTRONICA LAMB
        INSERT INTO VENTA_ELECTRONICA_ACES(EMISORID,ORIGENID,FECHA,TIPO,NROCOMPROBANTE,COMPROBANTE,NOMBREARCHIVO,PROCESADO,ENVIADO)
        VALUES (L_EMISOR_ID,P_ID_VENTA,clock_timestamp(),L_ID_COMPROBANTE,L_SERIE||'-'||L_NUMERO,L_DET,L_RUC||'-'||L_ID_COMPROBANTE||'-'||L_SERIE||'-'||L_NUMERO||'.txt',0,0);

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
