SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_sales_sp_venta_electronica_nube (P_ID_VENTA bigint) AS $body$
DECLARE

        L_ID_ENTIDAD bigint;
        L_RUC varchar(11);
        L_ID_COMPROBANTE varchar(2);
        L_SERIE varchar(4);
        L_NUMERO varchar(8);
        L_DET varchar(4000);
        L_EMISOR_ID bigint:=1; --EMPRESA CON RUC UPeU
        L_HASH varchar(50);
        	

BEGIN  
        SELECT 
                ID_ENTIDAD,ID_COMPROBANTE,SERIE,NUMERO INTO STRICT L_ID_ENTIDAD,L_ID_COMPROBANTE,L_SERIE,L_NUMERO
        FROM VENTA
        WHERE ID_VENTA = P_ID_VENTA;

        SELECT B.ID_RUC, EMISORID INTO STRICT L_RUC, L_EMISOR_ID
        FROM CONTA_ENTIDAD A, CONTA_EMPRESA B
        WHERE A.ID_EMPRESA = B.ID_EMPRESA
        AND A.ID_ENTIDAD = L_ID_ENTIDAD;

        IF L_ID_COMPROBANTE = '03' OR L_ID_COMPROBANTE = '01' THEN
            SELECT (pkg_sales_fc_venta(P_ID_VENTA)) INTO STRICT L_DET;
        END IF;
        IF L_ID_COMPROBANTE = '07' THEN
            SELECT (pkg_sales_fc_nota_credito(P_ID_VENTA)) INTO STRICT L_DET;
        END IF;
        --FACTURACION ELECTRONICA LAMB
        INSERT INTO VENTA_ELECTRONICA_NUBE(EMISORID,ORIGENID,FECHA,TIPO,NROCOMPROBANTE,COMPROBANTE,NOMBREARCHIVO)
        VALUES (L_EMISOR_ID,P_ID_VENTA,clock_timestamp(),L_ID_COMPROBANTE,L_SERIE||'-'||L_NUMERO,L_DET,L_RUC||'-'||L_ID_COMPROBANTE||'-'||L_SERIE||'-'||L_NUMERO||'.txt');
        --HACEMOS UNA PAUSA DE 2 SEGUNDOS PARA DEVOLVER EL VALOR RESUMEN
        --PKG_SALES.SP_SLEEP(2);
        --APEX_UTIL.PAUSE(4);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
