SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales,public;




CREATE OR REPLACE PROCEDURE eliseo.pkg_sales_sp_venta_electronica (P_ID_VENTA bigint,P_ID_COMPROBANTE text) AS $body$
DECLARE

        L_ID_ENTIDAD bigint;
        L_ID_DEPTO varchar(25);
        L_RUC varchar(11);
        L_ID_COMPROBANTE varchar(2);
        L_SERIE varchar(4);
        L_NUMERO varchar(8);
        L_ES_EXPORTACION varchar(1);

        L_DET varchar(4000);
        --L_DET CLOB;
        L_EMISOR_ID bigint:=1; --EMPRESA CON RUC UPeU
        L_HASH varchar(50);
        	

BEGIN  
        SELECT 
                ID_ENTIDAD,ID_DEPTO,ID_COMPROBANTE,SERIE,NUMERO , ES_EXPORTACION
                INTO STRICT L_ID_ENTIDAD,L_ID_DEPTO,L_ID_COMPROBANTE,L_SERIE,L_NUMERO, L_ES_EXPORTACION
        FROM VENTA
        WHERE ID_VENTA = P_ID_VENTA;

        SELECT B.ID_RUC INTO STRICT L_RUC
        FROM CONTA_ENTIDAD A, CONTA_EMPRESA B
        WHERE A.ID_EMPRESA = B.ID_EMPRESA
        AND A.ID_ENTIDAD = L_ID_ENTIDAD;

         ---- AR01: SE AGREGÃ“ ESTO PARA INGRESAR DATOS A COLUMNAS NUEVAS EN VENTA 
         IF P_ID_COMPROBANTE='03' THEN
            UPDATE venta
            SET
               -- id_tipodocumento_cli = DECODE(LENGTH(FC_DOCUMENTO_CLIENTE(ID_CLIENTE)),8,1,0) ,
                id_tipodocumento_cli = CASE WHEN LENGTH(pkg_sales_fc_documento_cliente_venta(ID_CLIENTE,id_comprobante))=8 THEN 1  ELSE 0 END ,
               -- nro_doc_cli = DECODE(LENGTH(FC_DOCUMENTO_CLIENTE(ID_CLIENTE)),8,NVL(FC_DOCUMENTO_CLIENTE(ID_CLIENTE),'999'),'999'),
               nro_doc_cli = CASE WHEN LENGTH(pkg_sales_fc_documento_cliente_venta(ID_CLIENTE,id_comprobante))=8 THEN coalesce(pkg_sales_fc_documento_cliente_venta(ID_CLIENTE,id_comprobante),'999')  ELSE '999' END ,
                razon_social_cli = coalesce(trim(both FC_NOMBRE_CLIENTE(ID_CLIENTE)),'Cliente Bazar'),
                direccion_cli = REGEXP_REPLACE(coalesce(trim(both pkg_sales_fc_cliente_direccion(ID_CLIENTE)),'Sin Direccion'),'-','', 'g')
            WHERE
                id_venta = P_ID_VENTA;
         ELSIF P_ID_COMPROBANTE = '01' THEN
                UPDATE venta
                SET
                    id_tipodocumento_cli = '6',
                    nro_doc_cli = coalesce(fc_documento_cliente(coalesce(id_cliente_legal, id_cliente)), '-'),
                    razon_social_cli = fc_nombre_cliente(coalesce(id_cliente_legal, id_cliente))
                WHERE
                    id_venta = P_ID_VENTA;
         END IF;
         --- AR01: FIN
        
        IF (P_ID_COMPROBANTE = '03' OR P_ID_COMPROBANTE = '01' )THEN
            SELECT (pkg_sales_fc_venta(P_ID_VENTA)) INTO STRICT L_DET;
            --L_DET:= '';
        END IF;
        IF (P_ID_COMPROBANTE = '07' OR P_ID_COMPROBANTE = '08' )THEN
            SELECT (pkg_sales_fc_nota_credito(P_ID_VENTA)) INTO STRICT L_DET;
        END IF;

        /** AREGLO POR LA UPN */

        IF L_ID_ENTIDAD=7124 AND L_ID_DEPTO='3' THEN   -- SOLO PARA IMPRENTA
        	IF (P_ID_COMPROBANTE = '03' OR P_ID_COMPROBANTE = '01' )THEN
	            IF L_ES_EXPORTACION='1' THEN 
	            	SELECT (pkg_sales_industry_fc_venta_exportacion(P_ID_VENTA)) INTO STRICT L_DET;
	            ELSE
	            	SELECT (pkg_sales_industry_fc_venta(P_ID_VENTA)) INTO STRICT L_DET;
	            END IF;
	        END IF;
	        IF (P_ID_COMPROBANTE = '07' OR P_ID_COMPROBANTE = '08' )THEN
	            SELECT (pkg_sales_industry_fc_nota(P_ID_VENTA)) INTO STRICT L_DET;
	        END IF;
        END IF;

        /**  */

       
        --FACTURACION ELECTRONICA LAMB
        INSERT INTO VENTA_ELECTRONICA(EMISORID,ORIGENID,FECHA,TIPO,NROCOMPROBANTE,COMPROBANTE,NOMBREARCHIVO)
        VALUES (L_EMISOR_ID,P_ID_VENTA,clock_timestamp(),L_ID_COMPROBANTE,L_SERIE||'-'||L_NUMERO,L_DET,L_RUC||'-'||L_ID_COMPROBANTE||'-'||L_SERIE||'-'||L_NUMERO||'.txt');
        --FACTURACION ELECTRONICA CONTAWEB
        --INSERT INTO UPEU_COMPROBANTE@DBL_ARON_APP(EMISORID,ORIGENID,FECHA,TIPO,NROCOMPROBANTE,COMPROBANTE,NOMBREARCHIVO)
        --VALUES(L_EMISOR_ID,P_ID_VENTA,SYSDATE,L_ID_COMPROBANTE,L_SERIE||'-'||L_NUMERO,L_DET,L_RUC||'-'||L_ID_COMPROBANTE||'-'||L_SERIE||'-'||L_NUMERO||'.txt');
        --HACEMOS UNA PAUSA DE 2 SEGUNDOS PARA DEVOLVER EL VALOR RESUMEN
        --PKG_SALES.SP_SLEEP(2);
        --APEX_UTIL.PAUSE(4);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eliseo.pkg_sales_sp_venta_electronica (P_ID_VENTA bigint,P_ID_COMPROBANTE text) FROM PUBLIC;
