CREATE OR REPLACE FUNCTION eliseo.pkg_sales_facturacion_1_fc_cliente_direccion (P_ID_PERSONA bigint) RETURNS varchar AS $body$
DECLARE

    L_DIRECCION varchar(1000);

BEGIN
	    L_DIRECCION:='';
        SELECT Min(coalesce(DIRECCION,'')) INTO STRICT L_DIRECCION
        FROM MOISES.PERSONA_DIRECCION
        WHERE ID_PERSONA = P_ID_PERSONA
        AND ID_TIPODIRECCION = 5;

        if coalesce(L_DIRECCION::text, '') = '' then 
                SELECT Min(coalesce(DIRECCION,'')) INTO STRICT L_DIRECCION
                FROM MOISES.PERSONA_DIRECCION
                WHERE ID_PERSONA = P_ID_PERSONA
                AND ID_TIPODIRECCION = 4;
        end if;

        RETURN(L_DIRECCION);
    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
