CREATE OR REPLACE FUNCTION eliseo.pkg_sales_school_fc_depto_alumno_school_inst (P_ID_ALUMNO bigint, P_ID_ENTIDAD bigint, P_ID_DEPTO text) RETURNS varchar AS $body$
DECLARE

  l_depto varchar(10);
  l_contar bigint;
  l_id_pngrado bigint;
  l_id_institucion bigint;
  l_depto_administracion varchar(10);

BEGIN

select max(ai.id_institucion), max(ai.id_depto_administracion) into STRICT l_id_institucion, l_depto_administracion 
from jose.SCHOOL_INSTITUCION ai 
where AI.id_depto = P_ID_DEPTO
and ai.id_CAMPO = P_ID_ENTIDAD;

SELECT count(1), max(dep.ID_DEPTO)
  into STRICT l_contar, l_depto
  FROM jose.SCHOOL_ALUMNO sa, jose.SCHOOL_INSTITUCION_NIVEL_DEPTO dep
           WHERE l_id_institucion = dep.ID_INSTITUCION
           AND sa.ID_NIVEL = dep.ID_NIVEL
           AND ID_ALUMNO = P_ID_ALUMNO;
  IF l_contar < 1 then
	  IF coalesce(l_depto_administracion::text, '') = '' THEN 
		    l_depto := P_ID_DEPTO;
	  ELSE
	  	  l_depto := l_depto_administracion;
	  END IF;
  end if;
  return l_depto;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
