SET client_encoding TO 'UTF8';
SET search_path = eliseo,pkg_sales_school,public;




CREATE OR REPLACE FUNCTION eliseo.pkg_sales_school_fc_depto_alumno_school_oth_sch (P_ID_ALUMNO bigint, P_ID_ENTIDAD bigint, P_ID_DEPTO text, P_ID_DEPTO_DESTINO text) RETURNS varchar AS $body$
DECLARE

  l_depto varchar(10);
  l_contar bigint;
  l_id_pngrado bigint;
  l_id_institucion bigint;
  l_id_institucion_destino bigint;
  l_id_nivel bigint;
  l_depto_administracion_origen varchar(10);
  l_depto_administracion_destino varchar(10);

BEGIN

-- INSTITUCION ORIGEN 
select max(ai.id_institucion), max(i.id_depto_administracion) into STRICT l_id_institucion, l_depto_administracion_origen from jose.SCHOOL_ALUMNO_INSTITUCION ai 
inner join jose.school_institucion i on ai.id_institucion = i.id_institucion 
where i.id_depto = P_ID_DEPTO
AND I.ID_CAMPO = P_ID_ENTIDAD
and ai.id_alumno = P_ID_ALUMNO;

-- institucion destino 
select max(i.id_institucion), max(i.id_depto_administracion) into STRICT l_id_institucion_destino, l_depto_administracion_destino
from jose.school_institucion i 
where i.id_depto = P_ID_DEPTO_DESTINO
AND I.ID_CAMPO = P_ID_ENTIDAD;

-- Nivel Origen
SELECT max(ID_NIVEL) into STRICT l_id_nivel FROM jose.SCHOOL_ALUMNO sa
WHERE ID_ALUMNO = P_ID_ALUMNO;

-- depto
SELECT count(1), max(dep.ID_DEPTO)
  into STRICT l_contar, l_depto
  FROM jose.SCHOOL_INSTITUCION_NIVEL_DEPTO dep
           WHERE l_id_institucion_destino = dep.ID_INSTITUCION
           AND l_id_nivel = dep.ID_NIVEL;

  IF l_contar < 1 then 
    --  l_depto := P_ID_DEPTO_DESTINO;
    IF coalesce(l_depto_administracion_destino::text, '') = '' THEN 
		    l_depto := P_ID_DEPTO_DESTINO;
	  ELSE
	  	  l_depto := l_depto_administracion_destino;
	  END IF;

  end if;

  return l_depto;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
