CREATE OR REPLACE FUNCTION eliseo.utils_convert_to_timestamp (P_EXPR text , P_PRECISION bigint DEFAULT 0, P_SCALE bigint DEFAULT 0, P_STYLE bigint DEFAULT NULL) RETURNS TS AS $body$
DECLARE

   v_format varchar(50);

BEGIN
    IF (P_STYLE IS NOT NULL AND P_STYLE::text <> '') THEN
      V_FORMAT := utils_get_format_from_style(P_STYLE);
      RETURN to_timestamp(P_EXPR, V_FORMAT);
    ELSE
      RETURN utils_convert_string_to_timestamp(P_EXPR);
    END IF;
EXCEPTION
    WHEN OTHERS THEN
      RAISE EXCEPTION '%', DBMS_UTILITY.FORMAT_ERROR_STACK USING ERRCODE = '45000';
END;
