CREATE OR REPLACE FUNCTION eliseo.utils_ident_incr (p_sequence text) RETURNS bigint AS $body$
DECLARE

    v_incr_by bigint;

BEGIN
    SELECT increment_by INTO STRICT v_incr_by
       FROM USER_SEQUENCES
       WHERE sequence_name LIKE UPPER(p_sequence);

