CREATE OR REPLACE FUNCTION eliseo.utils_radians (p_degree bigint) RETURNS bigint AS $body$
DECLARE

    v_rad bigint;

BEGIN
    v_rad := p_degree / 180 * utils_pi();

    IF position('.' in p_degree::text) = 0 THEN
        v_rad := FLOOR(v_rad);
    ELSE
        v_rad := round((p_degree / 180 * utils_pi())::numeric,18);
    END IF;

    RETURN v_Rad;
EXCEPTION
    WHEN OTHERS THEN
      RAISE EXCEPTION '%', DBMS_UTILITY.FORMAT_ERROR_STACK USING ERRCODE = '45000';
END;
