CREATE OR REPLACE FUNCTION eliseo.utils_reverse_ (p_expr text) RETURNS varchar AS $body$
DECLARE

    v_result varchar(2000) := NULL;
BEGIN
    FOR i IN 1..LENGTH(p_expr) LOOP
      v_result := v_result || SUBSTR(p_expr, -i, 1);
    END LOOP;

    RETURN v_result;
EXCEPTION 
    WHEN OTHERS THEN
      RAISE EXCEPTION '%', DBMS_UTILITY.FORMAT_ERROR_STACK USING ERRCODE = '45000';
END;
