CREATE OR REPLACE PROCEDURE eliseo.iudp_aps_asist_marcaciones (P_iIdMarcacion integer, P_iIdTerminal integer,P_iCodUsuario integer, P_FechaHora text) AS $body$
DECLARE


S_CANTIDAD            integer;

BEGIN
    SELECT COUNT(*) INTO STRICT S_CANTIDAD FROM APS_Asist_Marcaciones WHERE iIdMarcacion = P_iIdMarcacion;

    IF S_CANTIDAD = 0 
    THEN
      INSERT INTO APS_Asist_Marcaciones(iIdMarcacion, iIdTerminal, iCodUsuario, FechaHora)
        VALUES (P_iIdMarcacion, P_iIdTerminal, P_iCodUsuario, to_date(P_FechaHora,'yyyy-mm-dd hh24:mi:ss'));
    ELSE
      UPDATE APS_Asist_Marcaciones SET
        iIdTerminal = P_iIdTerminal,
        iCodUsuario = P_iCodUsuario,
        FechaHora   = to_date(P_FechaHora,'yyyy-mm-dd hh24:mi:ss')
      WHERE iIdMarcacion = P_iIdMarcacion;
    END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
