CREATE OR REPLACE PROCEDURE eliseo.spc_sta_ayudas_desc_rt ( p_id_entidad integer, p_id_entidad_assinet integer, p_id_anho integer, p_id_mes integer, p_id_persona integer, p_id_ctacte text, OUT CURSOR REFCURSOR ) AS $body$
DECLARE


    l_ingresos decimal(38,2);
    l_codigo_assinet varchar(20):='';


BEGIN
    DELETE FROM TT_TABLE_AUX;

	SELECT MAX(CODIGO_ASSINET) INTO STRICT l_codigo_assinet
  	FROM moises.PERSONA_NATURAL pn WHERE ID_PERSONA = p_id_persona;

    OPEN CURSOR FOR 
    SELECT 
        	to_char(FEC_ASIENTO, 'DD/MM/YY') AS FECHA,
            COMENTARIO,
            COS_VALOR as valor
        from VW_CONTA_DIARIO
        where id_entidad = p_id_entidad
        and ID_CUENTAAASI = '2135002'
        and (ID_CTACTE = p_id_ctacte OR ID_CTACTE = l_codigo_assinet)
        and ID_MES = p_id_mes
        and ID_ANHO = p_id_anho
        AND NOT COMENTARIO LIKE 'APS%'
        AND HABER=0 
;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
