CREATE OR REPLACE PROCEDURE eliseo.spc_sta_salary_desc_cta_total ( p_id_entidad integer, p_id_anho integer, p_id_mes integer, p_id_ctacte text, p_id_cuentaaasi text, OUT CURSOR REFCURSOR ) AS $body$
DECLARE


    l_ingresos decimal(38,2);

l_id_persona bigint;


BEGIN
	
	SELECT max(ID_PERSONA) INTO STRICT l_id_persona FROM MOISES.PERSONA_DOCUMENTO WHERE NUM_DOCUMENTO =p_id_ctacte;


    OPEN CURSOR FOR
    SELECT coalesce(sum(COS_VALOR),0) as total from (
   		 SELECT COS_VALOR from VW_CONTA_DIARIO
                where id_entidad = p_id_entidad
                and ID_CUENTAAASI = p_id_cuentaaasi
                and ID_CTACTE = p_id_ctacte
                and ID_MES = p_id_mes
                and ID_ANHO = p_id_anho
                and not ID_TIPOASIENTO = 'RH'
                -- Le estamos agregardo el descuento a la boleta de Ayudas y Viajes(1557, 1558)
                  
UNION all

                SELECT 
                COS_VALOR
                FROM VW_APS_PLANILLA A INNER JOIN APS_CONCEPTO_PLANILLA B
                 ON A.ID_CONCEPTOAPS = B.ID_CONCEPTOAPS 
				WHERE A.ID_ENTIDAD = p_id_entidad
				AND A.ID_ANHO = p_id_anho
				AND A.ID_MES = p_id_mes
				AND A.ID_PERSONA = l_id_persona
				AND A.ID_CONCEPTOAPS IN (1557, 1558) 
                ) x;

  
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eliseo.spc_sta_salary_desc_cta_total ( p_id_entidad integer, p_id_anho integer, p_id_mes integer, p_id_ctacte text, p_id_cuentaaasi text, OUT CURSOR REFCURSOR ) FROM PUBLIC;

