CREATE OR REPLACE PROCEDURE eliseo.spc_sta_salary_desc_total ( p_id_entidad integer, p_id_anho integer, p_id_mes integer, p_id_persona integer, p_tipo text, OUT CURSOR REFCURSOR ) AS $body$
DECLARE


    l_desc_extra decimal(38,2);
    l_num_documento varchar(100);


BEGIN
    DELETE FROM TT_TABLE_AUX;
    l_desc_extra := 0;
    select max(num_documento) into STRICT l_num_documento from (
    SELECT num_documento from persona_documento
    where id_persona = p_id_persona
    order by id_persona desc
    ) b LIMIT 1;


    select coalesce(sum(COS_VALOR),0) into STRICT l_desc_extra from VW_CONTA_DIARIO
                where ID_CUENTAAASI = '2135001'
                and id_entidad = 17112
                and ID_ANHO = 2017
                and ID_MES = 7
                and ID_TIPOASIENTO = 'MI'
                and COD_AASI = 3
                and id_entidad = p_id_entidad
                and ID_ANHO = p_id_anho
                and id_mes = p_id_mes
                and ID_CTACTE = l_num_documento;
    OPEN CURSOR FOR
    SELECT 
                coalesce(sum(p.COS_VALOR),0) + l_desc_extra as total
                from
                VW_APS_PLANILLA p, APS_CONCEPTO_PLANILLA a
                where p.ID_CONCEPTOAPS=a.ID_CONCEPTOAPS
                and p.ID_ENTIDAD=p_id_entidad
                and p.ID_ANHO=p_id_anho
                and p.ID_MES= p_id_mes
                and p.ID_PERSONA=p_id_persona
                and (
                  P.ID_CONCEPTOAPS like '15%'
                  or P.ID_CONCEPTOAPS like '7600%'
                )
                and not P.ID_CONCEPTOAPS like '1552%'
                and not P.ID_CONCEPTOAPS like '1556%'
                and not P.ID_CONCEPTOAPS like '1557%'
                and not P.ID_CONCEPTOAPS like '1558%';

  
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
