CREATE OR REPLACE PROCEDURE eliseo.spc_user_session_valida_app ( p_token text, OUT CURSOR REFCURSOR ) AS $body$
DECLARE

    l_id_user varchar(50) :='0';
    l_cant integer;

BEGIN

    update USERS_SESSION_ACADEMICO set LAST_UPDATE = current_timestamp where token = p_token and status = '1';

    select count(PER_ID) into STRICT l_cant
    from USERS_SESSION_ACADEMICO where token = p_token and status = '1';

    IF l_cant > 0 THEN
        select PER_ID into STRICT l_id_user
        from USERS_SESSION_ACADEMICO where token = p_token and status = '1';
    END IF;

    OPEN CURSOR FOR 
    SELECT CASE WHEN l_id_user='0' THEN 'NO'  ELSE 'SI' END  as active, l_id_user as id_user;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
