CREATE OR REPLACE VIEW vw_rep_deptos (id_nivel, id_parent, id_entidad, id_anho, id_tiporeporte, nombre, id_depto, nivel) AS SELECT A.ID_NIVEL,
            A.ID_PARENT,
            A.ID_ENTIDAD,
            B.ID_ANHO,
            A.ID_TIPOREPORTE,
            A.NOMBRE,
            B.ID_DEPTO,
            A.NIVEL
       FROM CONTA_NIVEL A, CONTA_NIVEL_DETALLE B
      WHERE A.ID_NIVEL = B.ID_NIVEL
   ORDER BY A.ID_NIVEL, A.NOMBRE;

