SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

CREATE OR REPLACE VIEW vw_saldo_alumno (id_entidad, id_depto, id_anho, id_cliente, credito, debito, saldo) AS SELECT ID_ENTIDAD,ID_DEPTO,ID_ANHO,ID_CLIENTE,
CASE WHEN SUM(TOTAL) < 0 THEN ABS(SUM(TOTAL)) ELSE 0 END AS CREDITO,
CASE WHEN SUM(TOTAL) > 0 THEN (SUM(TOTAL)) ELSE 0 END AS DEBITO,
SUM(TOTAL) AS SALDO
FROM (
        SELECT
        ID_ENTIDAD,ID_DEPTO,ID_ANHO,ID_CLIENTE,
        TOTAL
        FROM VW_SALES_MOV
        WHERE ID_TIPOVENTA IN (1,2,3,4) 
        
UNION ALL

        SELECT 
        ID_ENTIDAD,ID_DEPTO,ID_ANHO,ID_CLIENTE,
        SUM(IMPORTE)*CASE WHEN SIGN(SUM(IMPORTE))=1 THEN -1  ELSE 0 END  AS TOTAL
        FROM VW_SALES_ADVANCES
        GROUP BY ID_ENTIDAD,ID_DEPTO,ID_ANHO,ID_CLIENTE
) alias11 
GROUP BY ID_ENTIDAD,ID_DEPTO,ID_ANHO,ID_CLIENTE;

