CREATE OR REPLACE VIEW x_vw_sales_mov_col (id_entidad, id_depto, id_anho, id_mes, id_venta, id_cliente, id_sucursal, id_comprobante, id_moneda, serie, numero, fecha, glosa, total, total_me, id_tipotransaccion, id_tipoventa, tipo, mov) AS SELECT 	  A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          A.ID_VENTA,
          A.ID_CLIENTE,
          B.ID_SUCURSAL,
          A.ID_COMPROBANTE,
          A.ID_MONEDA,
          A.SERIE,
          A.NUMERO,
          A.FECHA,
          B.GLOSA,
          A.TOTAL,
          A.TOTAL_ME,
          B.ID_TIPOTRANSACCION,
          A.ID_TIPOVENTA,
          'V' AS TIPO,
          'SI: '||A.SERIE||'-'||A.NUMERO AS MOV
   FROM VENTA_SALDO A, VENTA B
   WHERE A.ID_VENTA = B.ID_VENTA
   AND A.ID_CLIENTE = B.ID_CLIENTE
   AND A.TOTAL <> 0

UNION ALL
 --VENTAS QUERY 1
    SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          A.ID_VENTA,
          A.ID_CLIENTE,
          A.ID_SUCURSAL,
          A.ID_COMPROBANTE,
          A.ID_MONEDA,
          A.SERIE,
          A.NUMERO,
          A.FECHA,
          A.GLOSA,
          A.TOTAL,
          A.TOTAL_ME,
          A.ID_TIPOTRANSACCION,
          A.ID_TIPOVENTA,
          'V' AS TIPO,
          'VNT: '||A.SERIE||'-'||A.NUMERO AS MOV
   FROM VENTA A
   WHERE ID_COMPROBANTE NOT IN ('07','08')
   AND ESTADO = 1
   -- AND A.TOTAL <> 0
 
   
UNION ALL
 --DEPOSITOS PAGAN VENTAS QUERY 1.1
   SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          C.ID_VENTA,
          A.ID_CLIENTE,
          C.ID_SUCURSAL,
          C.ID_COMPROBANTE,
          A.ID_MONEDA,
          C.SERIE,
          C.NUMERO,
          A.FECHA,
          A.GLOSA,
          B.IMPORTE * -1,
          B.IMPORTE_ME * -1,
          A.ID_TIPOTRANSACCION,
          C.ID_TIPOVENTA,
          'V' AS TIPO,
          'DEP: '||A.SERIE||'-'||A.NUMERO AS MOV
     FROM CAJA_DEPOSITO A, CAJA_DEPOSITO_DETALLE B, VENTA C
    WHERE A.ID_DEPOSITO = B.ID_DEPOSITO AND B.ID_VENTA = C.ID_VENTA AND A.ID_CLIENTE = C.ID_CLIENTE AND B.IMPORTE <> 0
    
UNION ALL
 --NOTAS DE CREDITO AFECTAN A VENTAS QUERY 1.2
    SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          B.ID_VENTA,
          A.ID_CLIENTE,
          B.ID_SUCURSAL,
          B.ID_COMPROBANTE,
          A.ID_MONEDA,
          B.SERIE,
          B.NUMERO,
          A.FECHA,
          A.GLOSA,
          CASE WHEN A.ID_COMPROBANTE='07' THEN -1  ELSE 1 END *A.TOTAL AS TOTAL,
          CASE WHEN A.ID_COMPROBANTE='07' THEN -1  ELSE 1 END *A.TOTAL_ME AS TOTAL_ME,
          A.ID_TIPOTRANSACCION,
          B.ID_TIPOVENTA,
          'V' AS TIPO,
          'NCD: '||A.SERIE||'-'||A.NUMERO AS MOV
   FROM VENTA A, VENTA B
   WHERE A.ID_PARENT = B.ID_VENTA
   AND A.ID_CLIENTE = B.ID_CLIENTE
   AND A.ID_COMPROBANTE IN ('07','08')
   AND A.ESTADO = 1
   -- AND A.TOTAL <> 0
 
   
UNION ALL
 --TRANSFERENCIAS AFECTAN A VENTAS QUERY 1.3
   SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          C.ID_VENTA,
          A.ID_CLIENTE,
          C.ID_SUCURSAL,
          C.ID_COMPROBANTE,
          A.ID_MONEDA,
          C.SERIE,
          C.NUMERO,
          A.FECHA,
          A.GLOSA,
          CASE WHEN B.DC='C' THEN -1  ELSE 1 END *B.IMPORTE AS TOTAL,
          CASE WHEN B.DC='C' THEN -1  ELSE 1 END *B.IMPORTE_ME AS TOTAL_ME,
          A.ID_TIPOTRANSACCION,
          C.ID_TIPOVENTA,
          'V' AS TIPO,
          'TRAN: '||A.SERIE||'-'||A.NUMERO AS MOV
     FROM VENTA_TRANSFERENCIA A, VENTA_TRANSFERENCIA_DETALLE B, VENTA C
    WHERE A.ID_TRANSFERENCIA = B.ID_TRANSFERENCIA AND B.ID_VENTA = C.ID_VENTA AND A.ID_CLIENTE = C.ID_CLIENTE AND B.IMPORTE <> 0 AND A.ESTADO = '1'
   
UNION ALL
 --TRANSFERENCIAS AFECTAN A ENTRECLIENTES
   SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          C.ID_VENTA,
          A.ID_CLIENTE,
          C.ID_SUCURSAL,
          C.ID_COMPROBANTE,
          A.ID_MONEDA,
          C.SERIE,
          C.NUMERO,
          A.FECHA,
          A.GLOSA,
          CASE WHEN B.DC='C' THEN 1  ELSE -1 END *B.IMPORTE AS TOTAL,
          CASE WHEN B.DC='C' THEN 1  ELSE -1 END *B.IMPORTE_ME AS TOTAL_ME,
          A.ID_TIPOTRANSACCION,
          C.ID_TIPOVENTA,
          'V' AS TIPO,
          'TRAN: '||A.SERIE||'-'||A.NUMERO AS MOV
     FROM VENTA_TRANSFERENCIA A, VENTA_TRANSFERENCIA_DETALLE B, VENTA C
    WHERE A.ID_TRANSFERENCIA = B.ID_TRANSFERENCIA AND B.ID_VENTA = C.ID_VENTA AND A.ID_CLIENTE = C.ID_CLIENTE AND B.IMPORTE <> 0 AND A.ESTADO = '1'
    	AND A.ES_ENTRECLIENTES = 'S'

UNION ALL

--SALDO INICIAL SISTEMA EXTERNO QUERY 2
SELECT 	  A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          coalesce(A.ID_VENTA,ID_SALDO) AS ID_VENTA,
          A.ID_CLIENTE,
          NULL AS ID_SUCURSAL,
          A.ID_COMPROBANTE,
          A.ID_MONEDA,
          A.SERIE,
          A.NUMERO,
          A.FECHA,
          'Saldo Incial' AS GLOSA,
          A.TOTAL,
          A.TOTAL_ME,
          NULL AS ID_TIPOTRANSACCION,
          A.ID_TIPOVENTA,
          'S' AS TIPO,
          'SIN: '||A.SERIE||'-'||A.NUMERO AS MOV 
   FROM VENTA_SALDO A 
   WHERE coalesce(ID_VENTA::text, '') = ''
   AND A.TOTAL > 0 
   
UNION ALL
 --DEPOSITOS QUE PAGAN SALDOS QUERY 2.1
   SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          C.ID_SALDO AS ID_VENTA,
          A.ID_CLIENTE,
          null AS ID_SUCURSAL,
          C.ID_COMPROBANTE,
          A.ID_MONEDA,
          C.SERIE,
          C.NUMERO,
          A.FECHA,
          A.GLOSA,
          B.IMPORTE * -1,
          B.IMPORTE_ME * -1,
          A.ID_TIPOTRANSACCION,
          C.ID_TIPOVENTA,
          'S' AS TIPO,
          'DEP: '||A.SERIE||'-'||A.NUMERO AS MOV 
     FROM CAJA_DEPOSITO A, CAJA_DEPOSITO_DETALLE B, VENTA_SALDO C
    WHERE A.ID_DEPOSITO = B.ID_DEPOSITO AND B.ID_SALDO = C.ID_SALDO AND A.ID_CLIENTE = C.ID_CLIENTE
    AND ESTADO = '1' AND B.IMPORTE <> 0
    
UNION ALL
 --NOTAS DE CREDITO AFECTAN AL SALOD QUERY 2.2
    SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          B.ID_SALDO AS ID_VENTA,
          A.ID_CLIENTE,
          A.ID_SUCURSAL,
          B.ID_COMPROBANTE,
          A.ID_MONEDA,
          B.SERIE,
          B.NUMERO,
          A.FECHA,
          A.GLOSA,
          CASE WHEN A.ID_COMPROBANTE='07' THEN -1  ELSE 1 END *A.TOTAL AS TOTAL,
          CASE WHEN A.ID_COMPROBANTE='07' THEN -1  ELSE 1 END *A.TOTAL_ME AS TOTAL_ME,
          A.ID_TIPOTRANSACCION,
          A.ID_TIPOVENTA,
          'S' AS TIPO,
          'NCD: '||A.SERIE||'-'||A.NUMERO AS MOV 
   FROM VENTA A, VENTA_SALDO B
   WHERE A.ID_SALDO = B.ID_SALDO
   AND A.ID_CLIENTE = B.ID_CLIENTE
   AND A.ID_COMPROBANTE IN ('07','08')
   AND A.ESTADO = 1
   AND A.TOTAL <> 0 
    
UNION ALL

   SELECT  --TRANSFERENCIAS QUE AFECTAN AL SALDO QUERY 2.3
         A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          C.ID_SALDO AS ID_VENTA,
          A.ID_CLIENTE,
          NULL AS ID_SUCURSAL,
          C.ID_COMPROBANTE,
          A.ID_MONEDA,
          C.SERIE,
          C.NUMERO,
          A.FECHA,
          A.GLOSA,
          CASE WHEN B.DC='C' THEN  -1  ELSE 1 END  * B.IMPORTE AS TOTAL,
          CASE WHEN B.DC='C' THEN  -1  ELSE 1 END  * B.IMPORTE_ME AS TOTAL_ME,
          A.ID_TIPOTRANSACCION,
          C.ID_TIPOVENTA,
          'S' AS TIPO,
          'TRAN: '||A.SERIE||'-'||A.NUMERO AS MOV
     FROM VENTA_TRANSFERENCIA A
          JOIN VENTA_TRANSFERENCIA_DETALLE B
             ON A.ID_TRANSFERENCIA = B.ID_TRANSFERENCIA
          JOIN VENTA_SALDO C
             ON A.ID_CLIENTE = C.ID_CLIENTE AND B.ID_SALDO = C.ID_SALDO
    WHERE A.ESTADO = 1 AND A.ES_ANTICIPO IN ('S','N') --AND A.ES_ANTICIPO = 'N' 
    AND B.IMPORTE <> 0
    
UNION ALL
 --TRANSFERENCIAS QUE SON DOCUMENTOS A PAGAR QUERY 3
    SELECT DISTINCT A.ID_ENTIDAD,
                   A.ID_DEPTO,
                   A.ID_ANHO,
                   A.ID_MES,
                   A.ID_TRANSFERENCIA AS ID_VENTA,
                   A.ID_CLIENTE,
                   null AS ID_SUCURSAL,
                   '99' AS ID_COMPROBANTE,
                   A.ID_MONEDA,
                   A.SERIE,
                   A.NUMERO,
                   A.FECHA,
                   A.GLOSA,
                   A.IMPORTE AS TOTAL,
                   A.IMPORTE_ME AS TOTAL_ME,
                   A.ID_TIPOTRANSACCION,
                   A.ID_TIPOVENTA,
                   'T' AS TIPO,
                   'TRAN: '||A.SERIE||'-'||A.NUMERO AS MOV
     FROM VENTA_TRANSFERENCIA A
          JOIN VENTA_TRANSFERENCIA_DETALLE B
             ON A.ID_TRANSFERENCIA = B.ID_TRANSFERENCIA
    WHERE A.ES_ANTICIPO = 'N' AND coalesce(B.ID_VENTA::text, '') = '' AND coalesce(B.ID_SALDO::text, '') = '' AND coalesce(B.ID_TRANSFERENCIA_P::text, '') = ''
    AND A.ESTADO = 1 AND A.IMPORTE <> 0 
    
UNION ALL
 --DEPOSITOS PAGAN TRANSFERENCIAS QUERY 3.1
    SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          C.ID_TRANSFERENCIA AS ID_VENTA,
          A.ID_CLIENTE,
          null AS ID_SUCURSAL,
          '99' AS ID_COMPROBANTE,
          A.ID_MONEDA,
          C.SERIE,
          C.NUMERO,
          A.FECHA,
          A.GLOSA,
          B.IMPORTE * -1,
          B.IMPORTE_ME * -1,
          A.ID_TIPOTRANSACCION,
          C.ID_TIPOVENTA,
          'T' AS TIPO,
          'DEP: '||A.SERIE||'-'||A.NUMERO AS MOV
     FROM CAJA_DEPOSITO A, CAJA_DEPOSITO_DETALLE B, VENTA_TRANSFERENCIA C
    WHERE A.ID_DEPOSITO = B.ID_DEPOSITO AND B.ID_TRANSFERENCIA = C.ID_TRANSFERENCIA AND A.ID_CLIENTE = C.ID_CLIENTE
    AND A.ESTADO = '1' AND B.IMPORTE <> 0
    
UNION ALL
 --NOTAS DE CREDITO AFECTAN A TRANSFERENCIAS QUERY 3.2
    SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          B.ID_TRANSFERENCIA AS ID_VENTA,
          A.ID_CLIENTE,
          A.ID_SUCURSAL,
          '99' AS ID_COMPROBANTE,
          A.ID_MONEDA,
          B.SERIE,
          B.NUMERO,
          A.FECHA,
          A.GLOSA,
          CASE WHEN A.ID_COMPROBANTE='07' THEN -1  ELSE 1 END *A.TOTAL AS TOTAL,
          CASE WHEN A.ID_COMPROBANTE='07' THEN -1  ELSE 1 END *A.TOTAL_ME AS TOTAL_ME,
          A.ID_TIPOTRANSACCION,
          A.ID_TIPOVENTA,
          'T' AS TIPO,
          'NCD: '||A.SERIE||'-'||A.NUMERO AS MOV
   FROM VENTA A, VENTA_TRANSFERENCIA B
   WHERE A.ID_TRANSFERENCIA = B.ID_TRANSFERENCIA
   AND A.ID_CLIENTE = B.ID_CLIENTE
   AND A.ID_COMPROBANTE IN ('07','08')
   AND A.ESTADO = 1
   AND A.TOTAL <> 0 
    
UNION ALL

    SELECT A.ID_ENTIDAD, --ANTICIPO (AUTOMATICO) QUE PAGA UNA TRANSFERENCIA QUERY 3.3
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          C.ID_TRANSFERENCIA,
          A.ID_CLIENTE,
          NULL AS ID_SUCURSAL,
          '99' AS ID_COMPROBANTE,
          A.ID_MONEDA,
          C.SERIE,
          C.NUMERO,
          A.FECHA,
          A.GLOSA,
          CASE WHEN B.DC='C' THEN  -1  ELSE 1 END  * B.IMPORTE AS TOTAL,
          CASE WHEN B.DC='C' THEN  -1  ELSE 1 END  * B.IMPORTE_ME AS TOTAL_ME,
          A.ID_TIPOTRANSACCION,
          C.ID_TIPOVENTA,
          'T' AS TIPO,
          'TRAN: '||A.SERIE||'-'||A.NUMERO AS MOV
FROM VENTA_TRANSFERENCIA A JOIN VENTA_TRANSFERENCIA_DETALLE B
ON A.ID_TRANSFERENCIA = B.ID_TRANSFERENCIA
JOIN VENTA_TRANSFERENCIA C
ON A.ID_CLIENTE = C.ID_CLIENTE
AND B.ID_TRANSFERENCIA_P = C.ID_TRANSFERENCIA
WHERE  --A.ES_ANTICIPO = 'S'
--AND B.DC = 'C'
--AND 
A.ESTADO = 1
AND B.IMPORTE <> 0
    
UNION ALL
 
        SELECT A.ID_ENTIDAD,
          A.ID_DEPTO,
          A.ID_ANHO,
          A.ID_MES,
          C.ID_VENTA,
          B.ID_CLIENTE,
          C.ID_SUCURSAL,
          C.ID_COMPROBANTE,
          A.ID_MONEDA,
          C.SERIE,
          C.NUMERO,
          A.FECHA,
          B.DETALLE,
          B.IMPORTE,
          B.IMPORTE_ME,
          A.ID_TIPOTRANSACCION,
          C.ID_TIPOVENTA,
          'D' AS TIPO,
          'DEP: '||A.NUMERO AS MOV
     FROM CAJA_PAGO A
        INNER JOIN CAJA_PAGO_VENTA B ON A.ID_PAGO = B.ID_PAGO
        INNER JOIN VENTA C ON B.ID_VENTA = C.ID_VENTA
     WHERE A.ESTADO = '1';

