SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_CELULAR(P_ID_PERSONA NUMBER) RETURN VARCHAR2 IS
    L_CELULAR VARCHAR2(50);
    BEGIN 
       
		select 
		--case when CELULAR = 'N' then '' else CELULAR end 
		MAX(CELULAR)
		INTO L_CELULAR
		from moises.persona_natural where ID_PERSONA=P_ID_PERSONA;
	   --and rownum=1;
		IF L_CELULAR IS NULL THEN 
			SELECT
			        NVL(max(NUM_TELEFONO),'') INTO L_CELULAR
			FROM MOISES.PERSONA_TELEFONO pt 
			WHERE ID_PERSONA = P_ID_PERSONA
			AND ES_ACTIVO=1 and rownum=1;
		END IF;
    RETURN(L_CELULAR); 
END;

