FUNCTION        FC_CODIGO_ARTICULO(P_ID_ARTICULO NUMBER,P_TIPO VARCHAR2) RETURN VARCHAR2 IS
    L_CODIGO VARCHAR2(20);
    BEGIN 
    SELECT 
            DECODE(P_TIPO,'1',CODIGO,SUBSTR(CODIGO,1,8)) INTO L_CODIGO
    FROM INVENTARIO_ARTICULO
    WHERE ID_ARTICULO = P_ID_ARTICULO;
    RETURN(L_CODIGO); 
   
END;

