FUNCTION        FC_CODIGO_UNSP(
    P_ID_ARTICULO NUMBER)
  RETURN VARCHAR2
IS
  L_CODIGO VARCHAR2(8);
BEGIN
  SELECT a.CODIGO
  INTO L_CODIGO
  FROM eliseo.INVENTARIO_ARTICULO A
  INNER JOIN eliseo.INVENTARIO_ARTICULO B
  ON A.ID_ARTICULO   =B.ID_PARENT
  WHERE B.ID_ARTICULO=P_ID_ARTICULO;
  RETURN(L_CODIGO);
END;

