SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_COLP_OBT_DEPTO_ALMACEN(P_ID_ALMACEN NUMBER) RETURN NUMBER
IS
CONTAR INT:=0;
ID_DEPTO number:= NULL;

BEGIN
   SELECT COUNT(*) INTO CONTAR
		FROM INVENTARIO_ALMACEN IA
		INNER JOIN INVENTARIO_ALMACEN_RUBRO IR ON IA.ID_ALMACEN = IR.ID_ALMACEN
		WHERE IA.ESTADO='1' AND IR.ID_RUBRO=1 AND IA.ID_ALMACEN=P_ID_ALMACEN;

   IF CONTAR > 0 THEN
		SELECT IA.ID_DEPTO INTO ID_DEPTO
		FROM INVENTARIO_ALMACEN IA
		INNER JOIN INVENTARIO_ALMACEN_RUBRO IR ON IA.ID_ALMACEN = IR.ID_ALMACEN
		WHERE IA.ESTADO='1' AND IR.ID_RUBRO=1 AND IA.ID_ALMACEN=P_ID_ALMACEN;
   END IF;
	RETURN(ID_DEPTO);
END;

