FUNCTION        FC_CUO_CORRELATIVO(P_ID_MOV NUMBER, p_id_entidad NUMBER, p_id_anho NUMBER, p_id_mes number) RETURN VARCHAR2 AS
L_NOMBRE VARCHAR2(100);
BEGIN
	SELECT max('M'||NUM_AASI) INTO L_NOMBRE FROM VW_CONTA_DIARIO
	WHERE OBSERVACION = trim(to_char(P_ID_MOV,'9999999999999999999'))
	AND ID_ENTIDAD = p_id_entidad
	AND ID_ANHO = p_id_anho
	AND ID_MES = p_id_mes
	AND ID_CUENTAAASI = 2130101;
       
        RETURN(L_NOMBRE);
        
END;

