SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_DIAS_LABORABLES
 (fecha_inicio IN DATE, fecha_fin IN DATE)
  RETURN NUMBER IS
      numero_dias NUMBER := 0;
      fecha_actual DATE;
  BEGIN
      IF fecha_fin >= fecha_inicio THEN    
          fecha_actual := fecha_inicio;
          WHILE fecha_actual <= fecha_fin LOOP
             IF TO_CHAR(fecha_actual,'DY') NOT IN ('SÁB','DOM') THEN 
             	numero_dias := numero_dias + 1;
             END IF;
             fecha_actual := fecha_actual + 1;
         END LOOP;
         RETURN numero_dias;
     ELSE
     	RETURN 0;
     END IF;
 END FC_DIAS_LABORABLES;

