SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

function        FC_DINAMICA_ASIENTO_CTACTE(P_ID_ASIENTO INT) RETURN VARCHAR2
IS
s_ctacte varchar2(20);
s_resul varchar2(1000);
p_conta int;

CURSOR ASIENTO_CTACTE is
SELECT 
ID_CTACTE 
FROM CONTA_DINAMICA_CTA_CTE
WHERE ID_ASIENTO = P_ID_ASIENTO
ORDER BY ID_CTACTE;


BEGIN
    p_conta:=1;
    open ASIENTO_CTACTE;
        fetch ASIENTO_CTACTE into
            s_ctacte;
        while ASIENTO_CTACTE%found loop
            if p_conta = 1 then
                s_resul := s_ctacte;
            else
                s_resul := s_resul||', '||s_ctacte;
            end if;
                    
            fetch ASIENTO_CTACTE into
                s_ctacte;
            p_conta:=p_conta+1;
        end loop;
	close ASIENTO_CTACTE;	
	
	RETURN(s_resul);
END;

