SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_DOCUMENTO_CLIENTE(P_ID_CLIENTE NUMBER) RETURN VARCHAR2 AS
  l_datos VARCHAR2(250);
 cursor datos is
                SELECT  
                NUM_DOCUMENTO AS NOMBRE
                FROM MOISES.VW_PERSONA_NATURAL
                WHERE ID_PERSONA=P_ID_CLIENTE
                AND ID_TIPODOCUMENTO NOT IN (97,98) --DOCUMENTOS DE AFP - ONP
                UNION 
                SELECT 
                    ID_RUC AS NOMBRE
                FROM MOISES.VW_PERSONA_JURIDICA 
                WHERE ID_PERSONA=P_ID_CLIENTE;
        
BEGIN
       open datos;
       fetch datos into l_datos;
       if not datos%found then
         l_datos := '0000';
       end if;
       close datos;
       RETURN( l_datos );
       
        
END;

