FUNCTION        FC_ID_DIRECCION(P_ID_DIRECCION NUMBER) RETURN VARCHAR2 AS
  l_datos VARCHAR2(250);
 cursor datos is
                SELECT 
                    DIRECCION AS NOMBRE
                FROM MOISES.PERSONA_DIRECCION 
                WHERE ID_DIRECCION=P_ID_DIRECCION
                and ES_ACTIVO=1;

BEGIN
