SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION FC_ID_PROVEEDOR(P_ID_RUC NUMBER) RETURN NUMBER IS
    L_ID_PERSONA NUMBER;
BEGIN
        SELECT ID_RUC INTO L_ID_PERSONA FROM (
        SELECT ID_PERSONA AS ID_RUC
        FROM MOISES.VW_PERSONA_JURIDICA
        WHERE ID_RUC = P_ID_RUC
        UNION ALL
        SELECT ID_PERSONA AS ID_RUC
        FROM MOISES.VW_PERSONA_NATURAL
        WHERE NUM_DOCUMENTO = P_ID_RUC
        AND ID_TIPODOCUMENTO=6
         ) X WHERE ROWNUM =1;
        
        RETURN(L_ID_PERSONA);
END;

