SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION fc_lote_compra( p_idorigen IN NUMBER) RETURN VARCHAR2 IS
  p_nombre VARCHAR2(6);
  CURSOR dato  IS
    SELECT e.LOTE FROM eliseo.COMPRA d join eliseo.CONTA_VOUCHER e
on d.ID_VOUCHER=e.ID_VOUCHER
where d.ID_COMPRA=p_idorigen;
BEGIN
  OPEN dato;
  FETCH dato INTO p_nombre;
  IF NOT dato%found THEN
    p_nombre := ' ';
  END IF;
CLOSE dato;
RETURN(p_nombre);
END;

