SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_NAMEDEPTO(COD_ENTIDAD in varchar2) RETURN varchar2 IS
   p_name		  		   varchar2(25);
   cursor datos is
       Select
	       nombre
	   from conta_entidad
	   where ID_ENTIDAD = COD_ENTIDAD
     and ES_ACTIVO=1;
BEGIN
	 open datos;
	 fetch datos into p_name;
	 if not datos%found THEN 
	   p_name := '';
	 end if;
	 close datos;
	 RETURN( p_name );
END;

