SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION fc_nueva_glosa (
    p_id_asiento NUMBER
) RETURN VARCHAR2 IS

    l_glosa_old      VARCHAR2(200);
    l_glosa          VARCHAR2(200);
    l_id_tipo_origen VARCHAR2(50);
    l_id_origen      VARCHAR2(50);
    l_estado         VARCHAR2(5);
    l_estado_c       VARCHAR2(5);
    l_importe        NUMBER(10, 2);
    l_cuenta         VARCHAR2(10);
    l_asign          NUMBER;
BEGIN
    SELECT
        id_tipoorigen,
        id_origen,
        descripcion,
        cuenta,
        sign(importe)
    INTO
        l_id_tipo_origen,
        l_id_origen,
        l_glosa_old,
        l_cuenta,
        l_asign
    FROM
        conta_asiento
    WHERE
        id_asiento = p_id_asiento;

    l_estado_c :=
        CASE l_cuenta
            WHEN '1112001' THEN
                '1'
            WHEN '1112025' THEN
                '1'
            WHEN '2162001' THEN
                '1'
            ELSE '0'
        END;

    dbms_output.put_line('ESTADO:'
                         || l_estado
                         || '    TIPO:'
                         || l_id_tipo_origen
                         || '    SIGN:'
                         || l_asign);

    IF l_id_tipo_origen = '5' THEN -- GASTOS 
        IF l_asign = '1' THEN--D
            l_glosa := l_glosa_old;
        ELSIF l_asign = '-1' THEN--C  
            IF l_estado_c = '1' THEN
                SELECT
                    '5-'
                    || cef.id_banco
                    || '-'
                    || nvl(mp.id_mediopago, '00')
                    || '-'
                    || a.numero
                    || '-'
                    || to_char(a.fecha, 'DD/MM/YYYY')
                    || '-'
                    || 'TL-'
                    || substr(l_glosa_old, 8, 34)
                INTO l_glosa
                FROM
                         caja_pago a
                    INNER JOIN caja_cuenta_bancaria    ccb ON ccb.id_ctabancaria = a.id_ctabancaria
                    INNER JOIN caja_entidad_financiera cef ON cef.id_banco = ccb.id_banco
                    INNER JOIN caja_pago_gasto         cpg ON cpg.id_pago = a.id_pago
                    LEFT JOIN medio_pago              mp ON mp.id_mediopago = a.id_mediopago
                                               AND mp.id_mediopago IN ( '005', '006' )
                WHERE
                    cpg.id_pgasto = l_id_origen;

            ELSIF l_estado = '0' THEN
                l_glosa := l_glosa_old;
            END IF;
        END IF;
    ELSIF l_id_tipo_origen = '8' THEN --PAGO COMPRAS  
        IF l_asign = '1' THEN--D 
            l_estado :=
                CASE l_cuenta
                    WHEN '2130101' THEN
                        '1'
                    ELSE '0'
                END;
            IF l_estado = '1' THEN
                SELECT
                    fc_ruc_proveedor(cpc.id_proveedor)
                    || '/'
                    || nvl(c.serie, cs.serie)
                    || '-'
                    || lpad(nvl(c.numero, cs.numero), 8, '0')
                    || '/'
                    || substr(fc_nombre_cliente(cpc.id_proveedor), 0, 25)
                INTO l_glosa
                FROM
                    caja_pago_compra cpc
                    LEFT JOIN compra           c ON c.id_compra = cpc.id_compra
                    LEFT JOIN compra_saldo     cs ON cs.id_saldo = cpc.id_saldo
                WHERE
                    cpc.id_pcompra = l_id_origen;

            ELSIF l_estado = '0' THEN
                l_glosa := l_glosa_old;
            END IF;

        ELSIF l_asign = '-1' THEN--C 
            IF l_estado_c = '1' THEN
                SELECT
                    cef.id_banco
                    || '-'
                    || nvl(mp.id_mediopago, '00')
                    || '-'
                    || a.numero
                    || '-'
                    || to_char(a.fecha, 'DD/MM/YYYY')
                    || '-'
                    || 'TL-'
                    || substr(l_glosa_old, 8, 34)
                INTO l_glosa
                FROM
                         caja_pago a
                    INNER JOIN caja_cuenta_bancaria    ccb ON ccb.id_ctabancaria = a.id_ctabancaria
                    INNER JOIN caja_entidad_financiera cef ON cef.id_banco = ccb.id_banco
                    INNER JOIN caja_pago_compra        cpc ON cpc.id_pago = a.id_pago
                    LEFT JOIN medio_pago              mp ON mp.id_mediopago = a.id_mediopago
                                               AND mp.id_mediopago IN ( '005', '006' )
                WHERE
                    cpc.id_pcompra = l_id_origen;

            ELSIF l_estado_c = '0' THEN
                l_glosa := l_glosa_old;
            END IF;
        END IF;
    ELSIF l_id_tipo_origen = '9' THEN
        l_glosa := '9 XXXXXXX';
    ELSIF l_id_tipo_origen = '11' THEN  --DETRACCION 
        IF l_asign = '1' THEN--D 
            SELECT
                'DET-'
                || a.nro_operacion
                || '/'
                || fc_ruc_proveedor(a.id_proveedor)
                || '/'
                || nvl(c.serie, cs.serie)
                || '-'
                || lpad(nvl(c.numero, cs.numero), 8, '0')
                || '/'
                || substr(cdc.detalle, 36)
            INTO l_glosa
            FROM
                     caja_detraccion a
                INNER JOIN caja_detraccion_compra cdc ON cdc.id_detraccion = a.id_detraccion
                LEFT JOIN compra                 c ON c.id_compra = cdc.id_compra
                LEFT JOIN compra_saldo           cs ON cs.id_saldo = cdc.id_saldo
            WHERE
                a.id_detraccion = l_id_origen;

        ELSIF l_asign = '-1' THEN--C 
            IF l_estado_c = '1' THEN
                SELECT
                    cef.id_banco
                    || '-'
                    || nvl(mp.id_mediopago, '00')
                    || '-'
                    || a.nro_operacion
                    || '-'
                    || to_char(a.fecha_emision, 'DD/MM/YYYY')
                    || '-'
                    || 'Ruc:'
                    || fc_ruc_proveedor(a.id_proveedor) glosa
                INTO l_glosa
                FROM
                         caja_detraccion a
                    INNER JOIN caja_cuenta_bancaria    ccb ON ccb.id_ctabancaria = a.id_ctabancaria
                    INNER JOIN caja_entidad_financiera cef ON cef.id_banco = ccb.id_banco
                    LEFT JOIN medio_pago              mp ON mp.id_mediopago = a.id_mediopago
                                               AND mp.id_mediopago IN ( '005', '006' )
                WHERE
                    a.id_detraccion = l_id_origen;

            ELSIF l_estado_c = '0' THEN
                l_glosa := l_glosa_old;
            END IF;
        END IF;
    ELSIF l_id_tipo_origen = '12' THEN --RETENCION 
        IF l_asign = '1' THEN--D
            SELECT
                decode(cuenta
                       || '/'
                       || cuenta_cte, '2130390/1', '1', '0')
            INTO l_estado
            FROM
                conta_asiento
            WHERE
                id_asiento = p_id_asiento;

            IF l_estado = '1' THEN
                SELECT
                    'RET/'
                    || a.serie
                    || '-'
                    || lpad(a.nro_retencion, 8, '0')
                    || '/'
                    || fc_ruc_proveedor(a.id_proveedor)
                    || '/'
                    || nvl(c.serie, cs.serie)
                    || '-'
                    || lpad(nvl(c.numero, cs.numero), 8, '0')
                    || '/'
                    || substr(fc_nombre_cliente(a.id_proveedor), 25)
                INTO l_glosa
                FROM
                         caja_retencion a
                    INNER JOIN caja_retencion_compra crc ON crc.id_retencion = a.id_retencion
                    LEFT JOIN compra                c ON c.id_compra = crc.id_compra
                    LEFT JOIN compra_saldo          cs ON cs.id_saldo = crc.id_saldo
                WHERE
                    a.id_retencion = l_id_origen;

            ELSIF l_estado = '0' THEN
                l_glosa := l_glosa_old;
            END IF;

        ELSIF l_asign = '-1' THEN --C 
            IF l_estado_c = '1' THEN
                SELECT
                    '00-'
                    || nvl(mp.id_mediopago, '00')
                    || '-'
                    || a.nro_retencion
                    || '-'
                    || a.fecha_emision
                    || '-TL-'
                    || 'PAGO RET. '
                    || a.nro_retencion
                    || ' '
                    || substr(fc_nombre_cliente(a.id_proveedor), 25)
                INTO l_glosa
                FROM
                    caja_retencion a
                    LEFT JOIN medio_pago     mp ON mp.id_mediopago = a.id_mediopago
                                               AND mp.id_mediopago IN ( '005', '006' )
                WHERE
                    a.id_retencion = l_id_origen;

            ELSIF l_estado_c = '0' THEN
                l_glosa := l_glosa_old;
            END IF;

        END IF;
    ELSIF l_id_tipo_origen = '15' THEN --VALES
        IF l_asign = '1' THEN--D 
            l_glosa := l_glosa_old;
        ELSIF l_asign = '-1' THEN--C  
            IF l_estado_c = '1' THEN
                SELECT
                    '15-'
                    || cef.id_banco
                    || '-'
                    || nvl(mp.id_mediopago, '00')
                    || '-'
                    || a.numero
                    || '-'
                    || to_char(a.fecha, 'DD/MM/YYYY')
                    || '-'
                    || 'TL-'
                    || a.detalle glosa
                INTO l_glosa
                FROM
                         caja_vale a
                    INNER JOIN caja_cuenta_bancaria    ccb ON ccb.id_ctabancaria = a.id_ctabancaria
                    INNER JOIN caja_entidad_financiera cef ON cef.id_banco = ccb.id_banco
                    LEFT JOIN medio_pago              mp ON mp.id_mediopago = a.id_mediopago
                                               AND mp.id_mediopago IN ( '005', '006' )
                WHERE
                    a.id_vale = l_id_origen;

            ELSIF l_estado_c = '0' THEN
                l_glosa := l_glosa_old;
            END IF;

        END IF;
    ELSE
        l_glosa := 'XXXX';
    END IF;

    RETURN ( l_id_tipo_origen
             || '   - '
             || l_glosa );
END;

