SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_NUMTXT_CENTENAS(p_numero in number) RETURN varchar2 IS
   r_numero  varchar2(60);
   cursor numeros is
       Select
	       numtxt
	   from NUMEROS
	   where numero = mod(p_numero,1000)-mod(p_numero,100);
BEGIN

	 if mod(p_numero,1000) = 100 then
	 	 RETURN( 'cien ' );
	 end if;

	 open numeros;
	 fetch numeros into r_numero;

	 if numeros%notfound then
	 	r_numero := '';
	 end if;

	 close numeros;
	 RETURN( r_numero||' ' );
END;

