SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_NUMTXT_UNIDADES(p_numero in number) RETURN varchar2 IS
   r_numero  varchar2(60);
   cursor numeros is
       Select
	       numtxt
	   from NUMEROS
	   where numero = mod(p_numero,10);
BEGIN
	 r_numero := '';
	 if mod(p_numero,100)<10 or mod(p_numero,100)>19 then
	 	 open numeros;
		 fetch numeros into r_numero;
		 if numeros%notfound then
		 	r_numero := '';
		 end if;
		 close numeros;
	 end if;
	 RETURN( r_numero );
END;

