SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

function        FC_OBTENER_CELULAR_P(P_ID_PERSONA NUMBER) RETURN VARCHAR2
IS
l_dato VARCHAR2(100):='';
l_contar int:=0;
l_id number;

BEGIN

   select COUNT(*) INTO l_contar from MOISES.PERSONA_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA AND ID_TIPOTELEFONO IN (1,3,5) AND GTH = 1;

   IF l_contar>0 THEN
     IF l_contar=1 THEN
      select NUM_TELEFONO INTO l_dato from MOISES.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOTELEFONO IN (1,3,5) AND GTH = 1;
     ELSE
      select max(id_TELEFONO) into l_id from MOISES.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and ID_TIPOTELEFONO IN (1,3,5) AND GTH = 1;
      select NUM_TELEFONO INTO l_dato from MOISES.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and id_TELEFONO=l_id and ID_TIPOTELEFONO IN (1,3,5) AND GTH = 1;
     END IF;
  ELSE
  	select COUNT(*) INTO l_contar from MOISES.PERSONA_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA AND ID_TIPOTELEFONO IN (1,3,5);
	  	IF l_contar > 0 THEN
	  		SELECT max(id_TELEFONO) into l_id from MOISES.PERSONA_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA;
	  		SELECT NUM_TELEFONO INTO l_dato from MOISES.persona_TELEFONO WHERE ID_PERSONA=P_ID_PERSONA and id_TELEFONO=l_id;	
	  	END IF;
 	END IF;

	RETURN(l_dato);
END;

