SET client_encoding TO 'UTF8';

SET search_path = eliseo,public;
\set ON_ERROR_STOP ON

FUNCTION        FC_OBT_ALMACEN_AREA(P_AREA NUMBER) RETURN NUMBER
IS
CONTAR INT:=0;
ALMACEN number:= NULL;

BEGIN
   SELECT COUNT(*) INTO CONTAR
			FROM ORG_SEDE_AREA OSA
			INNER JOIN INVENTARIO_ALMACEN IA ON OSA.ID_SEDEAREA = IA.ID_SEDEAREA
			INNER JOIN INVENTARIO_ALMACEN_RUBRO IR ON IA.ID_ALMACEN = IR.ID_ALMACEN
			WHERE OSA.ID_AREA = P_AREA AND OSA.ESTADO='1' AND IA.ESTADO='1' AND ID_RUBRO=1;

   IF CONTAR > 0 THEN
		SELECT DISTINCT IA.ID_ALMACEN INTO ALMACEN
			FROM ORG_SEDE_AREA OSA
			INNER JOIN INVENTARIO_ALMACEN IA ON OSA.ID_SEDEAREA = IA.ID_SEDEAREA
			INNER JOIN INVENTARIO_ALMACEN_RUBRO IR ON IA.ID_ALMACEN = IR.ID_ALMACEN
			WHERE OSA.ID_AREA = P_AREA AND OSA.ESTADO='1' AND IA.ESTADO='1' AND ID_RUBRO=1;
   END IF;
	RETURN(ALMACEN);
END;

