FUNCTION        FC_PHONO(P_ID_PERSONA NUMBER) RETURN VARCHAR2 IS
    L_PHONO VARCHAR2(50);
    BEGIN 
       
    SELECT
            NVL(NUM_TELEFONO,'') INTO L_PHONO
    FROM PERSONA_TELEFONO
    WHERE ID_PERSONA = P_ID_PERSONA
    AND GTH = '1';
    RETURN(L_PHONO); 
END;

