FUNCTION        FC_PRIMA_INFANTIL(P_ID_ENTIDAD NUMBER, P_ID_PERSONA NUMBER, FECHA_INI DATE, FECHA_FIN DATE) RETURN NUMBER
IS
l_prima number(10,2):=0.00;
l_contar int:=0;
anho number;
mes number;
